    @Test
    public void csvToFile(){
        String path = "C:\\Users\\Administrator\\Desktop\\20211215.csv";
        String dir = "C:\\Users\\Administrator\\Desktop\\corpus";


        int order = 0;
        BufferedReader reader = null;
        String laststr = "";
        try{
            FileInputStream fileInputStream = new FileInputStream(path);
            InputStreamReader inputStreamReader = new InputStreamReader(fileInputStream, Charset.defaultCharset());
            reader = new BufferedReader(inputStreamReader);
            String tempString = null;
            while((tempString = reader.readLine()) != null){
                tempString = tempString.replace("\uFEFF", "").replace("\ufeff", "");
                if(StringUtils.isNotBlank(tempString) && tempString.matches("^(\\d+),NULL.*")){
                    makeTenderFile(laststr, dir, order);
                    laststr = "";
                    order++;
                }
                laststr += tempString+"\r\n";
            }
            reader.close();

            if(StringUtils.isNotBlank(laststr)){
                makeTenderFile(laststr, dir, order);
            }
        }catch(Exception e){
            e.printStackTrace();
        }finally{
            if(reader != null){
                try {
                    reader.close();
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
        }
    }

    private void makeTenderFile(String laststr, String dir, int order) {
        if(StringUtils.isBlank(laststr)){
            return;
        }

        try{
            String[] fields = laststr.split(",");
            String title = fields[4];
            //从后往前第一个html标签结尾的，属于招标内容
            int end=0;
            for(int i=fields.length-1;i>=0;i--){
                if(fields[i].endsWith(">")){
                    end = i;
                    break;
                }
            }

            StringBuffer sbf = new StringBuffer();
            sbf.append(title+"\r\n");
            for(int i=9; i==9 || i<=end; i++){
                String str = fields[i];
                if(StringUtils.isNotBlank(str) && !str.equals("NULL")){
                    if(i!=9){
                        sbf.append(",");
                    }
                    sbf.append(str);
                }
            }

            String filePath = dir+ File.separator + order+".txt";
            WriteFromFile.write(filePath, true, sbf.toString());
        }catch (Exception e){
            System.out.println("异常招标内容："+laststr);
            throw e;
        }
    }

